namespace Robot.Core.Common.Extensions{	public static class AnimationExtensions	{		public static double GetElapsedTime(this Lime.Animation animation)		{			foreach (var animationMarker in animation.Markers) {				if (animationMarker.Id.Equals(animation.RunningMarkerId)) {					return animation.Time - animationMarker.Time;				}			}			return 0;		}		public static bool IsCurrentAnimationAdvanced(this Lime.Animation animation)		{			return animation.GetElapsedTime() > 0;		}		public static void StopCurrentAnimation(this Lime.Animation animation)		{			animation.RunningMarkerId = null;		}	}}