using System;using System.Collections.Generic;using Lime;namespace Robot.Core.Common.Extensions{	public static class AssetBundleExtensions	{		public static IEnumerable<string> EnumerateFilesSafe(this AssetBundle value, string path)		{			IEnumerable<string> Filter(IEnumerable<string> paths)			{				foreach (string s in paths) {					if (s.StartsWith(path)) {						yield return s;					}				}			}			try {				return value.EnumerateFiles(path);			} catch (NotImplementedException) {				return Filter(value.EnumerateFiles());			}		}	}}